INSERT INTO `txp_css` (`name`, `css`, `file_mod_time`)
VALUES
	('default','/* ============================= */\n/* === styles ecran pour ======= */\n/* ======  MEMENTO  ============ */\n/* ¬© : Sacripant / Thomas Jund = */\n/* ============================= */\n\n/*Misc top\n-------------------------------*/\n/*reset des listes*/\n.nude			{ list-style-type: none; margin: 0; }\n.block		{ display: block; margin: 0; }\n\n/*Grilles\n-------------------------------*/\n/* table layout grid */\n.row {\n	display: table;\n	table-layout: fixed;\n/*	width: 100%;*/\n}\n.row > *,\n.col {\n	display: table-cell;\n	vertical-align: middle;\n	padding: 0.4em;\n}\n\n\n/* Sectionning\n-------------------------------*/\nbody:before {\n	content: \"\";\n	display: block;\n	width: 98%;\n	height: 120px;\n	background-color: hsl(190,100%,20%);\n	-webkit-transform: rotate(2deg);\n	   -moz-transform: rotate(2deg);\n	    -ms-transform: rotate(2deg);\n	     -o-transform: rotate(2deg);\n	        transform: rotate(2deg);\n	position: absolute;\n	top: -70px; left: 1%;\n	z-index: -1;\n}\n\n#keur				{/*id du body index*/}\n.site_wrapper	{ width: 940px; max-width: 95%; margin: 0 auto; }\n.site-header	{ padding-top: 10px; }\n.site_footer	{ }\n\n\n\n\n\n/*Site header\n-------------------------------*/\n.site-header			{ color: hsl(40, 10%, 90%); text-align: justify; }\n.site-header a			{ color: hsl(40, 10%, 90%); border: 0 none;}\n.site-header:after 	{\n	content: \"\";\n	width: 100%;\n	display: inline-block;\n}\n.site-header *		{ margin: 0; line-height: 1.1em; }\n.site-header > *	{ display: inline-block; vertical-align: text-bottom; }\n.brand				{ vertical-align: sub; }\n.site-name a		{ font-style: italic; /*color: inherit;*/ }\n.site-name:before {\n	content: attr(data-site-name);\n	position: absolute;\n	top: 0; left: 0; z-index: -1;\n	font-weight: 900;\n	color: hsl(190,100%,20%);\n	letter-spacing: -0.04em;\n}\n.infos-memento 			{\n	font-size: 0.4em;\n	border: 1px solid;\n	display: inline-block;\n	text-align: center;\n	width: 1.5em; height: 1.5em;\n	-webkit-background-clip: padding-box;\n	-webkit-border-radius: 1em;\n	   -moz-border-radius: 1em;\n	        border-radius: 1em;\n	position: relative;\n	top: -1em;	\n}\n.bascule-projet	{ text-align: left; max-width: 30%; }\n\n.whois-connected 	{  }\n.user-connected	{ display: inline; }	\n\n/*Main-section\n-------------------------------*/\n/*Login form*/\n.login-form		{ max-width:300px; background-color: hsl(40, 50%, 90%); padding: 1em; } \n\n/*Blocs*/\n.sidenav 		{ float: left; width: 5%; margin: 0; }\n.main-content 	{ float: left; width: 90%; margin: 0 0 3em 5%; min-height: 330px; }\n\n/*SideNav / TABS*/\n.li-sidenav		{ line-height: 1em; margin-bottom: 0.2em; }\n.li-sidenav:first-child { margin-top: -0.2em; }\n.a-sidenav		{ color: #fff; border: 0 none;}\n.fii				{ color: hsl(25,100%,50%); }\n	\n/*Main-content Misc*/\n.block-title { color: hsl(25,100%,50%); line-height: 1em; margin-bottom: 0.2em; } \n.block-title:before {\n	content: attr(data-this);\n	font-size: 2em;\n	position: absolute;\n	z-index: -1;\n	color: hsl(40, 15%, 85%);\n	font-weight: 800;\n	line-height: 1em;\n}\n\n/* PANES main-section section*/\n.pane {\n	clear: both;\n	/*display: none;*/\n	opacity: 0;\n	transform: translateY(-25px);\n	/*transform-origin: left top;*/\n	transition: all 600ms;\n/*	-webkit-transition: transform 400ms;\n		    transition: transform 400ms;*/\n}\n\n.pane-show {\n	/*display: block;*/\n	transform: translateY(0);\n	opacity: 1;\n}\n\n\n\n.id	{ font-style: italic; color: hsl(190, 20%, 55%); font-weight: 300;}\n\n/*Projets\n-------------------------------*/\n/*Archives projet*/\n/*.li-archive h2		{ width: 40px; }*/\n\n.ul-nav_archive\n, .archive-projet-meta\n{ white-space: nowrap; }\n\n.li-nav_archive	{ display: inline; }\n\n\n/*Projet header*/\n.projet-title					{ color: hsl(20, 100%, 50%); text-transform: none; font-weight:300; margin-bottom: 0; display: inline; }\n.projet-description			{ margin-left: 10%; }\n.projet-meta					{ display: inline; font-style: italic; }\n.projet-meta .edit-projet	{ vertical-align: super; }\n\n\n/*TODOS TABS\n-------------------------------*/\n\n/*table-todos-projet*/\ncol.todo-projet-id 		{ width:  6%; }\ncol.todo-projet-title 	{ width: 36%; }\ncol.todo-projet-cat 		{ width: 11%; }\ncol.todo-projet-start 	{ width: 10%; }\ncol.todo-projet-end 		{ width: 10%; }\ncol.todo-projet-temps	{ width: 10%; }\ncol.todo-projet-statut 	{ width: 12%; }\ncol.todo-projet-action	{ width:  5%; }\n\n/*table-todos-global*/\ncol.todo-globale-projet { width: 15%; }\ncol.todo-globale-title 	{ width: 34%; }\ncol.todo-globale-start 	{ width: 10%; }\ncol.todo-globale-end 	{ width: 10%; }\ncol.todo-globale-temps	{ width: 10%; }\ncol.todo-globale-statut { width: 12%; }\ncol.todo-globale-action	{ width:  8%; }\n\n/*Header*/\nth[class|=type]			{ cursor: pointer; white-space: nowrap; }\nth[class|=type]:after	{ \n	content :\" ‚ñº\";\n	color : ;\n	font-size: 0.8em;\n	opacity: 0.3;\n	display: inline-block;\n	-webkit-transition: all 400ms ease;\n	   -moz-transition: all 400ms ease;\n	    -ms-transition: all 400ms ease;\n	     -o-transition: all 400ms ease;\n	        transition: all 400ms ease; /* transition-property transition-duration(ms/s) transition-timing-function(ease-out/ease/linear/ease-in-out/ease-in/cubic-bezier(0.42, 0, 1.0, 1.0)/step-start/steps(3)/steps(3,start)/step-end) transition-delay(+-ms/s) */\n	\n\n	\n}\nth.asc:after,\nth.desc:after			{ 	\n	opacity: 1;\n	color: hsl(20,100%,50%);\n}\nth.desc:after				{\n	-webkit-transform: rotate(180deg);\n	   -moz-transform: rotate(180deg);\n	    -ms-transform: rotate(180deg);\n	     -o-transform: rotate(180deg);\n	        transform: rotate(180deg);\n}\n\n.parent-id	{ display: inline-block; width: 25%; font-size: 0.71429em; /*idem .small*/}\n.action 		{ vertical-align: middle; }\n\n/*cat√©gorie todos*/\n.en-cours,\n.programme			{ color: hsl(20, 70%, 40%); }\n.non-programme,\n.en-pause			{ color: hsla(20, 50%, 50%, 0.7); }\n.termine				{ color: hsla(190, 100%, 20%, 0.8); }\n.rejete				{ color: hsla(190, 100%, 20%, 0.5); text-decoration: line-through; }\n\n/*Highlight todo line when compteur ON*/\n.compteur-on	{\n	background-color: hsl(40, 50%, 98%);	\n	color: hsl(20, 70%, 45%);\n}\n.compteur-on .temps-reel	{\n	color: hsl(20, 100%, 35%);\n	font-weight: bold;\n}\n\n/*Edit-New-todo-form*/\n/*Inline Form*/\n.inline-form-wrapper {\n	padding: 0;\n	width: 100%;\n	min-width: 100%;\n	background-color: hsl(40, 70%, 80%);\n	color: hsl(190, 50%, 10%);\n}\n.inline-form-wrapper h2 {\n	margin-left: 5px;\n}\n.inline-form-wrapper tr:hover {\n	background-color: transparent;\n} \n\ncol.edit-todo-id			{ width:  4%; }\ncol.edit-todo-title		{ width: 37%; }\ncol.edit-todo-cat			{ width: 12%; }\ncol.edit-todo-date		{ width: 20%; }\ncol.edit-todo-temps		{ width: 10%; }\ncol.edit-todo-statut		{ width: 12%; }\ncol.edit-todo-action		{ width:  5%; }\n\ntd.edit-todo-date label	{ display: inline-block; width: 30%; }			\ntd.edit-todo-date input	{ display: inline-block; width: 70%; }\n\ncol.edit-new-title 	{ width: 45%; }       	\ncol.edit-new-cat     { width: 18%; }\ncol.edit-new-temps   { width: 13%; }\ncol.edit-new-statut  { width: 18%; }\ncol.edit-new-action  { width:  6%; }\n\n\n\n/* COMMENTAIRES TAB\n---------------------*/\n/*formulaire et preview*/\n#comment-form	{\n	max-height: 0px;\n	overflow: hidden;\n	\n	-webkit-transition: all 400ms ease-in;\n	   -moz-transition: all 400ms ease-in;\n	    -ms-transition: all 400ms ease-in;\n	     -o-transition: all 400ms ease-in;\n	        transition: all 400ms ease-in; /* transition-property transition-duration(ms/s) transition-timing-function(ease-out/ease/linear/ease-in-out/ease-in/cubic-bezier(0.42, 0, 1.0, 1.0)/step-start/steps(3)/steps(3,start)/step-end) transition-delay(+-ms/s) */\n	\n}\n#comment-form.open	{\n	max-height: 350px;\n	padding-top: 1em;\n}\n#cpreview				{ float: right; width: 49%; overflow: auto; height: 350px; }\n#txpCommentInputForm	{ float: left; width: 49%; }\n/*comments*/\n.comments-list 		{\n	list-style-type: none;\n	counter-reset: comment-count;\n}\n.comment				{\n	background-color: hsl(40, 12%, 94%);\n	padding: 0.4em;\n	position: relative;\n}\n.comments-list .comment:before {\n	counter-increment: comment-count;\n	content : counter(comment-count);\n	position: absolute;\n	right: 99%;\n	width: 1.5em;\n	height: 1.5em;\n	background-color: hsl(40, 100%, 50%);\n	line-height: 1.5em;\n	text-align: center;\n	-webkit-background-clip: padding-box; /* fix WebKit background bleed on border-radius */\n	-webkit-border-radius: 1em;\n	   -moz-border-radius: 1em;\n	        border-radius: 1em;\n	color: hsl(40, 12%, 90%);\n	font-weight: bold;\n	font-size: 1.35714em;\n	\n}\n\n.comment-anchor 	{\n	position: absolute;\n	font-size: 3em;\n	font-weight: bold;\n/*	color: hsl(38, 14%, 85%);*/\n	opacity: 0.15;\n	-moz-opacity: 0.15;\n	filter:alpha(opacity=15);\n	z-index: 2;\n	margin: -0.5em 0 0 -0.4em;\n}\n.comment-anchor a { border-bottom: 0 none; }\n.comment-data		{\n	text-align: right; margin: 0.4em 0; line-height: 1em;\n}\n.comment-data time 	{ font-style: italic; }\n.comment-body h1		{\n	font-size: 1.35714em; font-weight: bold;\n}\n.comment-body h2		{ font-size: 1.35714em; } \n\n\n/* ATTACHMENTS / FILES / LINKS\n-----------------------------*/\n\n#attachments form {\n	float: right;\n	width: 31.33%;\n	margin-left: 3%;\n}\n\n.attachments-content {\n	overflow: hidden;\n}\n.file_download_description { margin-bottom: 0; }\n.file-metas { float: right; }\n\n/*Progress bar*/\n.file-form-info		{ margin: 0.5em 0; }\n.progressBar			{ min-width: 98%; }\n\n/* ADRESSE TAB\n-----------------------------*/\n.vcards-list {\n	word-spacing: 1em;\n}\n.vcard	{\n	display: inline-block;\n	vertical-align: top;\n	padding: 1em;\n	background-color: hsla(40, 100%, 50%, .15);\n	word-spacing: normal;\n	margin-bottom: 1em;\n	border: 1px solid #fff;\n	position: relative;\n}\n.vcard * {}\n.org		{ font-weight: 500; }\n.vcard p .picto {\n	filter:alpha(opacity=40);\n			-moz-opacity: 0.4;\n				  opacity: 0.4;\n	vertical-align: top;\n}\n\n.download-vcard {\n	position: absolute;\n	right: 1em; bottom: 1em;\n}\n\n/*FINANCE / DEVIS\n-------------------*/\n\n\n#finance		{  }\n.devis-section		{ clear: both; }\n.devis-cat-title	{  }\n.item-devis			{ list-style-type: none; clear: both; height: 0.9em; border-bottom: 1px dotted; margin-bottom: 0.5em; }\n.item-devis-title	{ float: left; padding-right: 0.4em; background-color: hsl(40, 10%, 90%); font-weight: normal; }\n.item-devis-liste	{ margin-left: 1em; }\n.montant				{ float: right; text-align: right; padding-left: 0.4em; background-color: hsl(40, 10%, 90%); }\n\n.sous-total			{ font-style: italic; margin-top: 1em; }\n.total				{ font-weight: 400; }\n\n\n/*Images et galerie\n--------------------------------*/\n.thumb {/*pour toutes les vignettes*/}\n.fullsize {/*pour toutes les img full size*/}\n        \n.wrap-galerie {/*ul ou ol de galerie images*/}\n.li-galerie {}\n.a-galerie {}\n\n/*footer\n-------------------------------*/\n#nav_footer {}\n.li-nav_footer {}\n.a-nav_footer {}\n\n/*misc\n-------------------------------*/\n\n/*\n** Icones\n-------------------------------\ninfo 				: &#x2139	&#8505;			Printer		: &#59158;\nuser 				: &#59394;					Crayon		: &#9998;\ndisconnect/close	: &#10006\nliste 				: &#9776;\ncommentaires		: &#59392;\nfichiers li√©s 		: &#x1F4CE\nbourse dollars 		: &#x1f4b0\nvcard 				: &#59170;\n-------------------------------*/\n[data-picto]:before {\n	font-family: \'PictosMemento\';\n	content: attr(data-picto);\n}\n.picto strong {\n/*	visuallyhidden*/\n	border:0;clip:rect(0 0 0 0);height:1px;margin:-1px;overflow:hidden;padding:0;position:absolute;width:1px;\n}\n\n/*Boutons*/\n\n.btn,\n.btn-wrap > * {\n	font-size: 0.9em;\n	color: hsl(40, 10%, 60%);\n	background-color: #fff;\n	\n	border: 1px solid hsl(40, 11%, 90%);\n	-webkit-background-clip: padding-box; /* fix WebKit background bleed on border-radius */\n	-webkit-border-radius: 0.4em;\n	   -moz-border-radius: 0.4em;\n	        border-radius: 0.4em;\n	\n	padding: 0em 0.2em 0.2em;	\n}\n\n.btn:hover, .btn:focus,\n.btn-wrap > *:hover, .btn-wrap > *:focus \n.compteur-on .action-start	{\n	color: hsl(40, 10%, 30%);\n	border-color: hsl(40, 10%, 20%);\n}\n.btn:active,\n.btn-wrap > *:active\n.compteur-on .action-start {\n	-webkit-box-shadow:   inset 0px 0px 2px 0px hsl(25, 100%, 30%);\n	   -moz-box-shadow:   inset 0px 0px 2px 0px hsl(25, 100%, 30%);\n	        box-shadow:   inset 0px 0px 2px 0px hsl(25, 100%, 30%); /* inset offset-x offset-y blur spread color */	\n}\n.btn:disabled,\n.btn-wrap > *:disabled		{\n	background: -moz-repeating-linear-gradient(125deg,hsl(40, 11%, 75%), hsl(40, 11%, 95%) 3px, hsl(40, 11%, 75%) 6px);\n	background: -webkit-repeating-linear-gradient(125deg,hsl(40, 11%, 75%), hsl(40, 11%, 95%) 3px, hsl(40, 11%, 75%) 6px);\n	background: repeating-linear-gradient(125deg,hsl(40, 11%, 75%), hsl(40, 11%, 95%) 3px, hsl(40, 11%, 75%) 6px);\n}\n\n/*\n** DateInput Jquey Tools\n-------------------------------*/\n/* the input field */\n.date {}\n\n/* calendar root element */\n#calroot {\n	z-index:10000;\n	margin-top:-5px;\n	padding:0.3em;\n	background-color:rgba(255,255,255,0.7);\n\n	font-size:0.8em;\n	\n	-webkit-box-shadow: 0em 0.3em 0.5em hsl(190,100%,10%);\n	   -moz-box-shadow: 0em 0.3em 0.5em hsl(190,100%,10%);\n	        box-shadow: 0em 0.3em 0.5em hsl(190,100%,10%);\n	\n	\n}\n#calhead a	{ border-bottom: 0 none; }\n\n#calhead			{ text-transform: uppercase; }\n#caltitle 			{\n    float:left;\n    text-align:center;\n    width:155px;\n    text-shadow:0 1px 0 #ddd;\n}\n#calnext, #calprev {\n    display:block;\n    width:20px;\n    height:20px;\n    float:left;\n    cursor:pointer;\n}\n#calprev:before	{ content:\"‚ó§\"; } \n#calnext:before	{ content:\"‚ó•\"; } \n#calnext 		{ float:right; text-align: right;}\n\n#calprev.caldisabled, #calnext.caldisabled {}\n\n/* year/month selector */\n#caltitle select {}\n\n/* names of the days */\n#caldays { clear: both; }\n\n#caldays span,\n.calweek a {\n	display: inline-block;\n	width: 14.285%;\n	text-align: right;\n	overflow: hidden;\n	padding: 0.2em 0.3em;\n	-webkit-box-sizing: border-box; \n	   -moz-box-sizing: border-box; \n	        box-sizing: border-box;\n	line-height: 1.5em;\n}\n\n/* container for weeks */\n#calweeks {}\n/* single week */\n.calweek { clear:left; line-height: 0; }\n/* single day */\n.calweek a {\n/*	border: 1px solid #000;*/\n	-webkit-background-clip: padding-box; /* fix WebKit background bleed on border-radius */\n	-webkit-border-radius: 0.4em;\n	   -moz-border-radius: 0.4em;\n	        border-radius: 0.4em;\n	background-color: hsl(190,20%,85%);\n	border: 1px solid transparent;\n}\n\n/* different states */\n.calweek a:hover, .calfocus {\n	background-color: hsl(190,65%,85%);\n	color: inherit;\n	border-color: hsl(190,65%,55%);\n}\n\n/* sunday */\n/* offmonth day */\na.calsunday,\na.caloff {\n	-moz-opacity: 0.6;\n	filter:alpha(opacity=60);\n	opacity: 0.6;\n}\n\na.caloff:hover { }\n\n/* unselecteble day */\na.caldisabled { }\n\n/* current day */\n#calcurrent { background-color:hsl(40,85%,65%); }\n\n/* today */\n#caltoday { border-color: hsl(190,65%,55%); background-color: hsl(190,65%,85%);}\n\n/*\n** Overlay Jquey Tools\n-------------------------------*/\n#overlay {\n	display:none;\n	z-index:10000;\n	background-color:#333;\n	width:675px;\n	min-height:200px;\n	border:1px solid #666; \n	/* CSS3 styling for latest browsers */\n	-webkit-box-shadow: 0 0 90px #000;\n	   -moz-box-shadow: 0 0 90px #000;\n	        box-shadow: 0 0 90px #000;\n	\n}\n','2015-11-23 16:37:44'),
	('forms','/* --------------------------------------------------------------\n\n   forms.css\n   * Sets up some default styling for forms\n   * Gives you classes to enhance your forms\n\n   Usage:\n   * For text fields, use class .title or .text\n   * For inline forms, use .inline (even when using columns)\n\n-------------------------------------------------------------- */\nlabel       { font-weight: 400; }\nfieldset    { padding:.4em; margin: 0 0 1.5em 0; border: 1px solid #ccc; }\nlegend      { padding: 0.5% 0.5%; white-space: nowrap; }\n\n/* Form fields\n-------------------------------------------------------------- */\ninput[type=text], input[type=password], textarea, select, input[type=file] {\n	background-color:#fff;\n	border:1px solid #fff;\n	min-width: 100%;\n	max-width: 100%;\n	padding:5px;\n	margin: 0 0 0.5em 0;\n	-moz-box-sizing: border-box; -ms-box-sizing: border-box; -webkit-box-sizing: border-box; -khtml-box-sizing: border-box; box-sizing: border-box;\n	opacity: 0.7;\n}\n\ninput[type=text]:focus, input[type=password]:focus,\ninput.text:focus, textarea:focus, select:focus {\n	border-color: hsl(40, 10%, 20%);\n	opacity: 1;\n}\ninput[type=checkbox], input[type=radio], input[type=submit], input[type=\"reset\"] {\n	min-width: 0;\n	max-width: none;\n}\ntextarea, .input-title input {\n	font: inherit;\n}\n\n.input-title input {\n	font-weight: bold;\n}\n\n/* Form item inline\n-------------------------------------------------------------- */\n\n.inline input[type=text], .inline input[type=password], .inline textarea, .inline select { \n	min-width: 0;\n	vertical-align: baseline;\n}\n.inline li {\n	margin-right: 1em; \n	float: left; \n	display: inline; \n}\n\n/* grille % formulaires sur 12 cols (cf. formee 1.2)\n-------------------------------------------------------------- */\n\n/* Field form */\n.form-1-12, .form-2-12, .form-3-12, .form-4-12, .form-5-12, .form-6-12, .form-7-12, .form-8-12, .form-9-12, .form-10-12, .form-11-12, .form-12-12 \n{ display:inline; float:left; margin-left:1%; margin-right:1%; padding: 0.5% 0.5% 0.8%; height:100%; }\n\n\n/* 12-column form (thanks for Fluid 960 form System) */\n.form-1-12  { width: 5.333%; }\n.form-2-12  { width: 13.666%; }\n.form-3-12  { width: 22%; }\n.form-4-12  { width: 30.333%; }\n.form-5-12  { width: 38.666%; }\n.form-6-12  { width: 47%; }\n.form-7-12  { width: 55.333%; }\n.form-8-12  { width: 63.666%; }\n.form-9-12  { width: 72%; }\n.form-10-12 { width: 80.333%; }\n.form-11-12 { width: 88.666%; }\n.form-12-12 { width: 97%; }\n\n/* hover effect */\n.form-1-12:hover, .form-2-12:hover, .form-3-12:hover, .form-4-12:hover, .form-5-12:hover, .form-6-12:hover, .form-7-12:hover, .form-8-12:hover, .form-9-12:hover, .form-10-12:hover, .form-11-12:hover, .form-12-12:hover {\n/*	background:#e5e5e5;*/\n}\n\n/* liste radio & checkbox */\n/* <ul class=\".form-list-chk\" > */\n.form-list-chk, .form-list-rdo {\n   height: 100%;\n	list-style: none outside none;\n	margin: 0;\n}\n\n/* Classes texpattern zem_contact\n-------------------------------------------------------------- */\n.zemText        { }\n.zemTextarea    { }\n.zemSelect      { }\n.zemRadio       { }\n.zemCheckbox    { }\n.zemSubmit      { }\n\n/* Success, notice and error boxes\n-------------------------------------------------------------- */\n.zemError, .zemThanks, .error,\n.notice,.success        { padding: .5em .5em .5em .5em; margin-bottom: 1em; border: 2px solid #ddd; }\n.zemError,.error        { background: #FBE3E4; color: #8a1f11; border-color: #FBC2C4; }\n.notice                 { background: hsl(40, 75%, 85%); border-color:hsl(40, 65%, 80%); color:hsl(40, 65%, 30%); }\n.success, .zemThanks    { background: #E6EFC2; color: #264409; border-color: #C6D880; }\n\n.error a, .zemError a   { color: #8a1f11; }\n.notice a               { }\n.success a              { color: #264409; }\n\n/* add * after required label */\nlabel.zemRequired:after, .required:after,\nlabel.zemRequirederrorElement:after\n						{ content: \" *\"; }\n/*Txp only*/\n.errorElement {}\nlabel.zemRequirederrorElement { color: #8a1f11; }\ninput.zemRequirederrorElement,\ntextarea.zemRequirederrorElement {\n	background-color: #FBE3E4;\n	border-color: #FBC2C4;\n}\n','2015-11-23 16:37:44'),
	('reset','/* -------------------------------------------------------------- \n  \n   reset.css for HTML5\n   M√©lange maison de boilerplate @ htlm5reset\n   \n-------------------------------------------------------------- */\n\nhtml, body, body div, span, object, iframe, h1, h2, h3, h4, h5, h6, p, blockquote, pre, abbr, address, cite, code, del, dfn, em, img, ins, kbd, q, samp, small, strong, sub, sup, var, b, i, dl, dt, dd, ol, ul, li, fieldset, form, label, legend, table, caption, tbody, tfoot, thead, tr, th, td, article, aside, figure, footer, header, hgroup, menu, nav, section, time, mark, audio, video {\n	margin: 0;\n	padding: 0;\n	border: 0;\n	outline: 0;\n	font-size: 100%;\n	vertical-align: baseline;\n	background: transparent;\n}\n/* =============================================================================\n   HTML5 display definitions\n   ========================================================================== */\n\narticle, aside, details, figcaption, figure, footer, header, hgroup, nav, section { display: block; }\naudio, canvas, video { display: inline-block; *display: inline; *zoom: 1; }\naudio:not([controls]) { display: none; }\n[hidden] { display: none; }\n\n/* Responsive images and other embedded objects\n   Note: keeping IMG here will cause problems if you\'re using foreground images as sprites.\n   If this default setting for images is causing issues, you might want to replace it with a .responsive class instead. */\nimg,object, embed { max-width: 100%; }\nimg { border: 0; -ms-interpolation-mode: bicubic; vertical-align: middle; }\n\n\n/* force a vertical scrollbar to prevent a jumpy page */\nhtml {font-size: 100%; -webkit-text-size-adjust: 100%; -ms-text-size-adjust: 100%; overflow-y: scroll;}\n\n/*NAV*/\nnav ul, nav ol { list-style: none; list-style-image: none; margin: 0; padding: 0; }\n\n/*RESET TYPO*/\nblockquote, q {quotes: none;}\nblockquote:before, blockquote:after, \nq:before, q:after {content: \'\'; content: none;}\n\n/* Redeclare monospace font family: h5bp.com/j */\npre, code, kbd, samp { font-family: monospace, serif; _font-family: \'courier new\', monospace; font-size: 1em; }\n/* Improve readability of pre-formatted text in all browsers */\npre { white-space: pre; white-space: pre-wrap; word-wrap: break-word; }\n\n/* Position subscript and superscript content without affecting line-height: h5bp.com/k */\nsub, sup { font-size: 75%; line-height: 0; position: relative; vertical-align: baseline; }\nsup { top: -0.5em; }\nsub { bottom: -0.25em; }\n\nsmall {font-size: 85%;}\n\nhr { display: block; height: 1px; border: 0; border-top: 1px solid #ccc; margin: 1em 0; padding: 0; }\n\n/* Tables still need \'cellspacing=\"0\"\' in the markup. */\ntable { border-collapse: separate; border-spacing: 0; }\ncaption, th, td { text-align: left; font-weight: normal; }\ntable, td, th { vertical-align: middle; }\n\n/*RESET FORMS*/\n/* hand cursor on clickable elements */\n.clickable, label, input[type=button], input[type=submit], button {cursor: pointer;}\nbutton[disabled], input[disabled] { cursor: default; }\nbutton, input, select, textarea { margin: 0; vertical-align: baseline; *vertical-align: middle; }\n\n/*MISC*/\n\n.clear      { clear: both; }\n.hide       { display: none; visibility: hidden; }\n/*CLEARFIX pour redonner 1 height aux blocs contenant des float*/\n.clearfix:before, .clearfix:after	{ content: \"\"; display: table; }\n.clearfix:after 							{ clear: both; }\n/* image replacement */\n.ir 			{ display: block; border: 0; text-indent: -999em; overflow: hidden; background-color: transparent; background-repeat: no-repeat; text-align: left; direction: ltr; }\n','2015-11-23 16:37:44'),
	('fonts','/* Pictos */\n@font-face {\n  font-family: \'PictosMemento\';\n  src: url(\"fonts/memento.eot\");\n  src: url(\"fonts/memento.eot?#iefix\") format(\'embedded-opentype\'), \n		 url(\"fonts/memento.woff\") format(\'woff\'), \n		 url(\"fonts/memento.ttf\") format(\'truetype\'), \n		 url(\"fonts/memento.svg#memento\") format(\'svg\');\n  font-weight: normal;\n  font-style: normal;\n}\n\n/* EXO */\n@font-face {\n    font-family: \'Exo\';\n    src: url(\'fonts/Exo-Thin-webfont.eot\');\n    src: url(\'fonts/Exo-Thin-webfont.eot?#iefix\') format(\'embedded-opentype\'),\n         url(\'fonts/Exo-Thin-webfont.woff\') format(\'woff\'),\n         url(\'fonts/Exo-Thin-webfont.ttf\') format(\'truetype\'),\n         url(\'fonts/Exo-Thin-webfont.svg#ExoThin\') format(\'svg\');\n    font-weight: 100;\n    font-style: normal;\n\n}\n\n@font-face {\n    font-family: \'Exo\';\n    src: url(\'fonts/Exo-Thin-Italic-webfont.eot\');\n    src: url(\'fonts/Exo-Thin-Italic-webfont.eot?#iefix\') format(\'embedded-opentype\'),\n         url(\'fonts/Exo-Thin-Italic-webfont.woff\') format(\'woff\'),\n         url(\'fonts/Exo-Thin-Italic-webfont.ttf\') format(\'truetype\'),\n         url(\'fonts/Exo-Thin-Italic-webfont.svg#ExoThinItalic\') format(\'svg\');\n    font-weight: 100;\n    font-style: italic;\n\n}\n\n@font-face {\n    font-family: \'Exo\';\n    src: url(\'fonts/Exo-ExtraLight-webfont.eot\');\n    src: url(\'fonts/Exo-ExtraLight-webfont.eot?#iefix\') format(\'embedded-opentype\'),\n         url(\'fonts/Exo-ExtraLight-webfont.woff\') format(\'woff\'),\n         url(\'fonts/Exo-ExtraLight-webfont.ttf\') format(\'truetype\'),\n         url(\'fonts/Exo-ExtraLight-webfont.svg#ExoExtraLight\') format(\'svg\');\n    font-weight: 200;\n    font-style: normal;\n\n}\n\n@font-face {\n    font-family: \'Exo\';\n    src: url(\'fonts/Exo-ExtraLight-Italic-webfont.eot\');\n    src: url(\'fonts/Exo-ExtraLight-Italic-webfont.eot?#iefix\') format(\'embedded-opentype\'),\n         url(\'fonts/Exo-ExtraLight-Italic-webfont.woff\') format(\'woff\'),\n         url(\'fonts/Exo-ExtraLight-Italic-webfont.ttf\') format(\'truetype\'),\n         url(\'fonts/Exo-ExtraLight-Italic-webfont.svg#ExoExtraLightItalic\') format(\'svg\');\n    font-weight: 200;\n    font-style: italic;\n\n}\n\n@font-face {\n    font-family: \'ExoLight\';\n    src: url(\'fonts/Exo-Light-webfont.eot\');\n    src: url(\'fonts/Exo-Light-webfont.eot?#iefix\') format(\'embedded-opentype\'),\n         url(\'fonts/Exo-Light-webfont.woff\') format(\'woff\'),\n         url(\'fonts/Exo-Light-webfont.ttf\') format(\'truetype\'),\n         url(\'fonts/Exo-Light-webfont.svg#ExoLight\') format(\'svg\');\n    font-weight: 300;\n    font-style: normal;\n\n}\n\n@font-face {\n    font-family: \'ExoLightItalic\';\n    src: url(\'fonts/Exo-Light-Italic-webfont.eot\');\n    src: url(\'fonts/Exo-Light-Italic-webfont.eot?#iefix\') format(\'embedded-opentype\'),\n         url(\'fonts/Exo-Light-Italic-webfont.woff\') format(\'woff\'),\n         url(\'fonts/Exo-Light-Italic-webfont.ttf\') format(\'truetype\'),\n         url(\'fonts/Exo-Light-Italic-webfont.svg#ExoLightItalic\') format(\'svg\');\n    font-weight: 300;\n    font-style: italic;\n\n}\n\n@font-face {\n    font-family: \'Exo\';\n    src: url(\'fonts/Exo-Regular-webfont.eot\');\n    src: url(\'fonts/Exo-Regular-webfont.eot?#iefix\') format(\'embedded-opentype\'),\n         url(\'fonts/Exo-Regular-webfont.woff\') format(\'woff\'),\n         url(\'fonts/Exo-Regular-webfont.ttf\') format(\'truetype\'),\n         url(\'fonts/Exo-Regular-webfont.svg#ExoRegular\') format(\'svg\');\n    font-weight: 400;\n    font-style: normal;\n\n}\n\n@font-face {\n    font-family: \'Exo\';\n    src: url(\'fonts/Exo-Regular-Italic-webfont.eot\');\n    src: url(\'fonts/Exo-Regular-Italic-webfont.eot?#iefix\') format(\'embedded-opentype\'),\n         url(\'fonts/Exo-Regular-Italic-webfont.woff\') format(\'woff\'),\n         url(\'fonts/Exo-Regular-Italic-webfont.ttf\') format(\'truetype\'),\n         url(\'fonts/Exo-Regular-Italic-webfont.svg#ExoRegularItalic\') format(\'svg\');\n    font-weight: 400;\n    font-style: italic;\n\n}\n\n@font-face {\n    font-family: \'Exo\';\n    src: url(\'fonts/Exo-Medium-webfont.eot\');\n    src: url(\'fonts/Exo-Medium-webfont.eot?#iefix\') format(\'embedded-opentype\'),\n         url(\'fonts/Exo-Medium-webfont.woff\') format(\'woff\'),\n         url(\'fonts/Exo-Medium-webfont.ttf\') format(\'truetype\'),\n         url(\'fonts/Exo-Medium-webfont.svg#ExoMedium\') format(\'svg\');\n    font-weight: 500;\n    font-style: normal;\n\n}\n\n@font-face {\n    font-family: \'ExoMediumItalic\';\n    src: url(\'fonts/Exo-Medium-Italic-webfont.eot\');\n    src: url(\'fonts/Exo-Medium-Italic-webfont.eot?#iefix\') format(\'embedded-opentype\'),\n         url(\'fonts/Exo-Medium-Italic-webfont.woff\') format(\'woff\'),\n         url(\'fonts/Exo-Medium-Italic-webfont.ttf\') format(\'truetype\'),\n         url(\'Exo-Medium-Italic-webfont.svg#ExoMediumItalic\') format(\'svg\');\n    font-weight: 500;\n    font-style: italic;\n\n}\n\n@font-face {\n    font-family: \'Exo\';\n    src: url(\'fonts/Exo-DemiBold-webfont.eot\');\n    src: url(\'fonts/Exo-DemiBold-webfont.eot?#iefix\') format(\'embedded-opentype\'),\n         url(\'fonts/Exo-DemiBold-webfont.woff\') format(\'woff\'),\n         url(\'fonts/Exo-DemiBold-webfont.ttf\') format(\'truetype\'),\n         url(\'fonts/Exo-DemiBold-webfont.svg#ExoDemiBold\') format(\'svg\');\n    font-weight: 600;\n    font-style: normal;\n\n}\n\n@font-face {\n    font-family: \'Exo\';\n    src: url(\'fonts/Exo-DemiBold-Italic-webfont.eot\');\n    src: url(\'fonts/Exo-DemiBold-Italic-webfont.eot?#iefix\') format(\'embedded-opentype\'),\n         url(\'fonts/Exo-DemiBold-Italic-webfont.woff\') format(\'woff\'),\n         url(\'fonts/Exo-DemiBold-Italic-webfont.ttf\') format(\'truetype\'),\n         url(\'fonts/Exo-DemiBold-Italic-webfont.svg#ExoDemiBoldItalic\') format(\'svg\');\n    font-weight: 600;\n    font-style: italic;\n\n}\n\n@font-face {\n    font-family: \'Exo\';\n    src: url(\'fonts/Exo-Bold-webfont.eot\');\n    src: url(\'fonts/Exo-Bold-webfont.eot?#iefix\') format(\'embedded-opentype\'),\n         url(\'fonts/Exo-Bold-webfont.woff\') format(\'woff\'),\n         url(\'fonts/Exo-Bold-webfont.ttf\') format(\'truetype\'),\n         url(\'fonts/Exo-Bold-webfont.svg#ExoBold\') format(\'svg\');\n    font-weight: 700;\n    font-style: normal;\n\n}\n\n@font-face {\n    font-family: \'Exo\';\n    src: url(\'fonts/Exo-Bold-Italic-webfont.eot\');\n    src: url(\'fonts/Exo-Bold-Italic-webfont.eot?#iefix\') format(\'embedded-opentype\'),\n         url(\'fonts/Exo-Bold-Italic-webfont.woff\') format(\'woff\'),\n         url(\'fonts/Exo-Bold-Italic-webfont.ttf\') format(\'truetype\'),\n         url(\'fonts/Exo-Bold-Italic-webfont.svg#ExoBoldItalic\') format(\'svg\');\n    font-weight: 700;\n    font-style: italic;\n\n}\n\n@font-face {\n    font-family: \'Exo\';\n    src: url(\'fonts/Exo-ExtraBold-webfont.eot\');\n    src: url(\'fonts/Exo-ExtraBold-webfont.eot?#iefix\') format(\'embedded-opentype\'),\n         url(\'fonts/Exo-ExtraBold-webfont.woff\') format(\'woff\'),\n         url(\'fonts/Exo-ExtraBold-webfont.ttf\') format(\'truetype\'),\n         url(\'fonts/Exo-ExtraBold-webfont.svg#ExoExtraBold\') format(\'svg\');\n    font-weight: 800;\n    font-style: normal;\n\n}\n\n@font-face {\n    font-family: \'Exo\';\n    src: url(\'fonts/Exo-ExtraBold-Italic-webfont.eot\');\n    src: url(\'fonts/Exo-ExtraBold-Italic-webfont.eot?#iefix\') format(\'embedded-opentype\'),\n         url(\'fonts/Exo-ExtraBold-Italic-webfont.woff\') format(\'woff\'),\n         url(\'fonts/Exo-ExtraBold-Italic-webfont.ttf\') format(\'truetype\'),\n         url(\'fonts/Exo-ExtraBold-Italic-webfont.svg#ExoExtraBoldItalic\') format(\'svg\');\n    font-weight: 800;\n    font-style: italic;\n\n}\n\n@font-face {\n    font-family: \'Exo\';\n    src: url(\'fonts/Exo-Black-webfont.eot\');\n    src: url(\'fonts/Exo-Black-webfont.eot?#iefix\') format(\'embedded-opentype\'),\n         url(\'fonts/Exo-Black-webfont.woff\') format(\'woff\'),\n         url(\'fonts/Exo-Black-webfont.ttf\') format(\'truetype\'),\n         url(\'fonts/Exo-Black-webfont.svg#ExoBlack\') format(\'svg\');\n    font-weight: 900;\n    font-style: normal;\n\n}\n\n@font-face {\n    font-family: \'Exo\';\n    src: url(\'fonts/Exo-Black-Italic-webfont.eot\');\n    src: url(\'fonts/Exo-Black-Italic-webfont.eot?#iefix\') format(\'embedded-opentype\'),\n         url(\'fonts/Exo-Black-Italic-webfont.woff\') format(\'woff\'),\n         url(\'fonts/Exo-Black-Italic-webfont.ttf\') format(\'truetype\'),\n         url(\'fonts/Exo-Black-Italic-webfont.svg#ExoBlackItalic\') format(\'svg\');\n    font-weight: 900;\n    font-style: italic;\n\n}\n\n','2015-11-23 16:37:44'),
	('typography','/* -------------------------------------------------------------- \n  \n\n	COULEURS\n	\n	vert : h 190\n		header hsl(190 100 25)\n		body	hsl(190 100 10)\n		id\n	\n	orange-rouge : h 20	\n	orange : h 25\n	jaune : h 40\n-------------------------------------------------------------- */\n\n/* -------------------------------------------------------------- \n\n	TYPO\n	\n	body font size : 14px\n	body line height : 1.5\n	Scale factor : 1.368\n	\n-------------------------------------------------------------- */\n\n\n/* Default font settings. \n   The font-size percentage is of 16px. (0.875 * 16px = 14px) */\nbody { \n  font-size: 87.5%; \n  color: hsl(190,100%,10%); \n  background: hsl(40, 10%, 90%);\n  font-family: \"Helvetica Neue\", Arial, Helvetica, sans-serif;\n}\n\n\n/* Headings\n-------------------------------------------------------------- */\n\nh1,h2,h3,h4,h5,h6,\n.h1,.h2,.h3,.h4,.h5,.h6,\nth,label,legend \n	{ font-weight: 200; font-family: Exo, Arial, Helvetica, sans-serif; }\n\nh1, .h1 { font-size: 3.5em; line-height: 1.28571em; margin-top: 0.4286em; margin-bottom: 0.8571em; position: relative; text-transform: uppercase; }\nh2, .h2 { font-size: 1.86em; line-height: 1.6em; margin-top: 1.6em; margin-bottom: 0.8em; }\nh3, .h3 { font-size: 1.35714em; line-height: 1.10526em; margin-top: 1.1053em; margin-bottom: 0em; }\nh4, .h4 { font-size: 1em; line-height: 1.5em; }\nh5 { font-size: 1em; font-weight: bold; margin-bottom: 1.5em; }\nh6 { font-size: 1em; font-weight: bold; }\n\n/* avoid collapsing margins on headings */\nh1:first-child, h2:first-child,\nh3:first-child, h4:first-child,\nh5:first-child, h6:first-child {\n	margin-top: 0;\n}\n\nh1 img, h2 img, h3 img, \nh4 img, h5 img, h6 img {\n  margin: 0;\n}\n\n\n/* Text elements\n-------------------------------------------------------------- */\n\np, .p       { font-size: 1em; margin: 0 0 1.5em; }\np img.left  { float: left; margin: 1.5em 1.5em 1.5em 0; padding: 0; }\np img.right { float: right; margin: 1.5em 0 1.5em 1.5em; }\n\na:focus, \na:hover     { color: hsl(25,100%,50%); border-bottom-style: solid; }\na           { color: inherit; text-decoration: none; border-bottom: 1px dashed;}\n\nblockquote  { margin: 1.5em; color: #666; font-style: italic; }\nstrong      { font-weight: bolder; }\nem,dfn      { font-style: italic; }\ndfn         { font-weight: bold; }\nsup, sub    { line-height: 0; }\n\nabbr, \nacronym     { border-bottom: 1px dotted #666; }\naddress     { margin: 0 0 1.5em; font-style: italic; }\ndel         { color:#666; }\n\npre 			{ margin: 1.5em 0; white-space: pre; }\npre,code,tt { font: 1em \'andale mono\', \'lucida console\', monospace; line-height: 1.5; }\n\n\n/* Lists\n-------------------------------------------------------------- */\n\nli ul, \nli ol       { margin:0 1.5em; }\nul, ol      { margin: 0 1.5em 1.5em 1.5em; }\n\nul          { list-style-type: disc; }\nol          { list-style-type: decimal; }\n\ndl          { margin: 0 0 1.5em 0; }\ndl dt       { font-weight: bold; }\ndd          { margin-left: 1.5em;}\n\n\n/* Tables\n-------------------------------------------------------------- */\n\ntable       			{ width:100%; table-layout: fixed; }\n/*col:nth-child(odd)	{ background-color: #ddd; }*/\ntbody tr:hover,\n.row:hover				{ background-color: hsla(40, 0%, 100%, 0.4); }\nth          			{ text-transform: uppercase; font-weight: 400; font-size: 0.9em; }\nth,td,caption 			{ padding: 5px; vertical-align: top; }\ntd 						{ overflow: hidden; text-overflow: ellipsis; }\n\n/*tr.even td  { background: #e5ecf9; }*/\ntfoot       { font-style: italic; }\ncaption     { text-transform: uppercase; color: #666; }\n\n\n/* Typo Misc classes\n-------------------------------------------------------------- */\n\n.small      { font-size: 0.71429em; }\n.small p 	{ margin: 0.5em 0 }\n.large      { font-size: 1.2em; line-height: 2.5em; margin-bottom: 1.25em; }\n','2015-11-23 16:37:44'),
	('print-preview','/* ================================= */\n/* = Print-preview & print finance = */\n/* ================================= */\n\nbody 	{ \n	font-family: \"PT Sans\", Arial, Helvetica, sans-serif;\n	font-size: 8pt;\n	line-height: 1.2em;\n	margin: 10mm 23.8% 30mm 7.15%;\n	color: #000;\n	background-color: transparent; \n}\n\n/*Misc*/\n.nude { list-style-type: none; margin: 0; }\n.white-mask { background: #fff; }\n\n\n/*#print			{ position: fixed; top 10px; right: 10px;}*/\n.metas-devis 	{ display: none; }\n\n/*Headers*/\nheader, h1, h2, h3, h4 {\n	font-family: \"PT Serif\", serif;\n	font-weight: normal;\n	line-height: normal;\n}\nh1	{ }\nh2	{ font-size: 12pt; font-style: italic; line-height: 1.2em; font-weight: bold; margin-bottom: 6pt; }\nh3	{ font-size: 10pt; font-weight: bold; }\nh4 { font-size: 10pt; }\n\nul {\n	list-style-position: inside;\n}\n\n/*Containers*/\n.site_wrapper {\n	width: 100%;\n	max-width: none;\n	argin: 0;\n}\n\n#devis, .facture {\n	border-bottom: 1px solid;\n	padding: 1em 0;\n}\n	\n/*Adresse Commanditaire*/\n.commanditaire-wrapper	{\n	float: right;\n	position: relative;\n}\n.commanditaire-wrapper h2 {\n	font-size: inherit;\n}\n.commanditaire-content {\n	border: 0.5pt dashed #000;\n	padding: 1em;\n	font-family: \"PT Sans Caption\", serif;\n}\n\n/*Titre document (devis,facture)*/\nh1	{\n	clear: both;\n	position: relative;\n	top: -40pt;\n	z-index: 100;\n}\n/*.finance-block-title {\n	font-size: 36pt;\n	font-weight: bold;\n	color: #fff;\n	-webkit-text-shadow: 0.5pt 0.5pt 0 #000, -0.5pt -0.5pt 0 #000;\n	   -moz-text-shadow: 0.5pt 0.5pt 0 #000, -0.5pt -0.5pt 0 #000;\n	        text-shadow: 0.5pt 0.5pt 0 #000, -0.5pt -0.5pt 0 #000;	\n}*/\n.no-devis {\n	display: block;\n	font-family: \"PT Mono\", serif;\n	font-size: 9pt;\n}\n\n/*Nature du Travail = Projet Header*/\n\narticle {\n	margin-left: 24mm;\n}\n.devis header {\n	margin-top: -20pt;\n}\narticle header {}\narticle header h2 {}\n.projet-title, .projet-description, .projet-description p {\n	display: inline;\n	font-size: 9pt;\n	line-height: 1.2em;\n}\n.projet-title {\n	font-weight: bold;\n	font-style: italic;\n	text-transform: uppercase;\n}\n\n\n/*d√©tail du Devis 	*/\n.details-prestation {\n	margin-top: 1em;\n}\n.devis-section		{\n	margin-left: .5cm;\n}\n\n.item-devis-liste	{ \n	margin-left: 1em;\n	font-size: 8pt;\n}\n\n\n/*Item-devis*/\n.item-devis	{ \n	clear: both;\n	position: relative; \n	/*height: 1em; */\n	/*border-bottom: 1px dotted; */\n	margin-bottom: 0.33em;\n	/*background-color: yellow;*/\n	padding-top: 0;\n	overflow: hidden;\n}\n\n.item-devis-title	{ \n	float: left; \n	padding-right: .4em; \n	/*background-color: #fff; */\n	font-weight: normal; \n	max-width: 80%;\n}\n.item-devis-title:first-letter	{ \n	text-transform: uppercase;\n}\n.item-devis-title:after {\n	content: \"0000\";\n	width: 100%;\n	height: 1em;\n	border-bottom: 1px dotted;\n	position: absolute;\n	top: 0;\n	left: 0;\n	z-index: -1;\n}\n\n\n.montant	{ \n	float: right; \n	text-align: right; \n	padding-left: 0.4em; \n	background-color: #fff; \n}\n\n\n.sous-total			{ font-style: italic; margin-top: 0.4em; }\n\n/*total Devis*/\n.total-groupe		{ font-size: 12pt; margin: 1em 0; }\n.total-groupe .montant { font-weight: bold; }\n.total-groupe h4 	{ margin-bottom: 0.5em; }\n.total-groupe .note	{ font-size: 8pt; margin-top: 12pt; }\n.rib				{ border: 1px dotted; padding: 1em; margin-top: 1em; }\n\n/*R√©partiion Factures*/\n.repartition	{ margin-bottom: 1em; }\n.repartition h3 { font-style: italic; }\n.devis-liste-factures {\n	border: 1px solid #999;\n	padding: 0.4em;\n}\n\n.footer-notes {\n	font-style: italic;\n}\n\n/* Specifique Factures */\n.rappel-presta {\n	padding: 1em;\n	border: 1pt solid #eee;\n	margin-bottom: 2em;\n}\n.rappel-total-presta .montant {\n	font-weight: normal;\n	font-size: 9pt;\n}\n\n.somme-a-payer h2 {\n	font-size: 14pt;\n}\n.somme-a-payer h4 {\n	font-size: 11pt;\n}\n\n\n/* Specifique PRINT */\n@media print {\n	body {\n		margin: 0;\n	}\n	\n	@page {\n		size: 210mm 297mm;\n		margin: 10mm 50mm 30mm 15mm ;\n		padding: 0;\n/*		margin: 15mm 45mm 45mm 45mm;*/\n		@bottom {\n/*			background-color: yellow;*/\n			content:url(../img/background-entete.svg);\n			text-align: left;\n/*			background-size:  cover;*/\n/*			width: 100%;*/\n		}\n	}\n	\n	#print {\n		display: none;\n	}\n	\n	#devis, .facture {\n		page-break-after: always;\n		border-bottom: 0 none;\n	}\n}\n\n','2016-11-21 18:29:08');
