VALUES
	('Links','link','<p><txp:link /><br />\n<txp:link_description /></p>','2015-11-23 16:37:44'),
	('comments','comment','<article>\n	<!-- add a permlink so people can link direct to this comment -->\n	<h3 class=\"comment-anchor\">\n		<txp:if_variable name=\"comment-preview\" value=\"no\">\n    		<txp:comment_permlink anchor=\"1\">#<txp:comment_id /></txp:comment_permlink>\n		<txp:else />\n			#Preview\n		</txp:if_variable>\n	</h3>\n	<h2 class=\"p comment-data\">\n		<span data-picto=\"Ó†Ç\" class=\"picto\"><strong>Utilisateur&nbsp;:</strong></span><txp:comment_name /> \n		| \n		<time datetime=\"<txp:comment_time format=\'iso8601\' />\" itemprop=\"commentTime\"><txp:comment_time />				\n				<txp:if_variable name=\"comment-preview\" value=\"no\">\n					(<txp:comment_time format=\"since\" />)\n				</txp:if_variable>\n		</time>\n	</h2>\n\n  <div itemprop=\"commentText\" class=\"comment-body\">\n    <txp:comment_message />\n  </div>\n</article>','2015-11-23 16:37:44'),
	('default','article','<txp:if_individual_article>\n<h1 class=\"entry-title\"><txp:permlink><txp:title /></txp:permlink></h1>\n<txp:else />\n<h3 class=\"entry-title\"><txp:permlink><txp:title /></txp:permlink></h3>\n</txp:if_individual_article>\n\n<p class=\"published\"><txp:posted /></p>\n\n<div class=\"entry-content\">\n<txp:body />\n</div>\n\n<address class=\"vcard author\">&#8212; <span class=\"fn\"><txp:author /></span></address>\n\n<p class=\"tags\"><txp:category1 title=\"1\" link=\"1\" />, <txp:category2 title=\"1\" link=\"1\" /></p>\n\n<txp:comments_invite wraptag=\"p\" />\n\n<div class=\"divider\"><txp:image id=\"1\" /></div>\n\n','2015-11-23 16:37:44'),
	('comment_form','comment','<txp:hide>\n============\nModule comment_form.comment appel√© depuis le module default.txp\n============\n</txp:hide>\n\n<txp:comments_error wraptag=\"ul\" break=\"li\" />\n\n<fieldset>\n	<legend>Ajouter un commentaire</legend>\n\n\n	<div class=\"hide\">\n		<txp:comment_name_input />\n	</div>\n	<div class=\"hide\">\n		<txp:comment_email_input />\n	</div>\n\n	<div class=\"form-item titre-comment input-title\">\n		<input type=\"text\" name=\"title-comment\" value=\"\" placeholder=\"Titre du commentaire\" />\n	</div>\n	\n	<div class=\"form-item\">\n		<txp:comment_message_input />\n	</div>\n	\n	<div class=\"notice small\">\n		<p>\n			Vous pouvez enrichir votre message √† l\'aide de la Syntaxe Textile. <a href=\"<txp:site_url />template/textile-reference/fr/index.html\" \n			   onclick=\"window.open(this.href,\'textile_reference\',\'height=400,width=600,scrollbars=1\'); return false;\">Aide Textile</a>.\n		</p>\n		<p>Vous devez pr√©visualiser votre message avant de pouvoir l\'envoyer.</p>\n	</div>\n	\n	<div class=\"btn-wrap\">\n		<txp:comment_preview />\n		<txp:comment_submit />\n	</div>\n</fieldset>\n','2015-11-23 16:37:44'),
	('files','file','<txp:text item=\"file\" />: \n<txp:file_download_link>\n<txp:file_download_name /> [<txp:file_download_size format=\"auto\" decimals=\"2\" />]\n</txp:file_download_link>\n<br />\n<txp:text item=\"category\" />: <txp:file_download_category /><br />\n<txp:text item=\"download\" />: <txp:file_download_downloads />','2015-11-23 16:37:44'),
	('comments_display','article','<txp:if_comments_allowed>\n	<button id=\"add-comment\" class=\"btn\" type=\"button\"><span class=\"picto\" data-picto=\"&#43;&#59160;\"><strong>Ajouter une tache</strong></span></button>\n	<div id=\"comment-form\" class=\"clearfix\">\n		<txp:if_comments_preview>\n			<txp:variable name=\"comment-preview\" value=\"yes\" />\n			<div id=\"cpreview\">\n				<txp:comments_preview wraptag=\"div\" class=\"comment-preview comment\" />\n			</div>	\n		</txp:if_comments_preview>\n		\n		<txp:comments_form /> 		\n	</div>\n</txp:if_comments_allowed>\n\n<txp:if_comments>	\n	<h2><txp:comments_count /> commentaire(s)</h2>\n	<txp:variable name=\"comment-preview\" value=\"no\" />\n	<txp:comments class=\"comments-list\" breakclass=\"comment\"/>\n	\n<txp:else />\n	<h2>Il n\'y a encore aucun commentaire</h2>\n</txp:if_comments>\n\n\n\n\n','2015-11-23 16:37:44'),
	('js_bottom','misc','<!-- Javascript bottom -->\n	<!-- Donn√©es Json de la page -->\n	<txp:output_form form=\"page-data-json\" />\n\n	<!-- <script src=\"<txp:site_url />template/js/jquery-1.7.2.min.js\"></script> -->\n	<script src=\"<txp:site_url />template/js/jquery-1.10.2.min.js\"></script>\n	<script src=\"<txp:site_url />/template/js/jquery.form.js\"></script>\n	\n	<!-- <script src=\"<txp:site_url />/template/js/jquery.tools.min.js\"></script> -->\n	<script src=\"<txp:site_url />/template/js/stupidtable.min.js\"></script>\n		\n	<script src=\"<txp:site_url />/template/js/sacripant.js\"></script>\n\n	<txp:cbe_frontauth_if_connected level=\"1\">\n			<script src=\"<txp:site_url />/template/js/finance.js\"></script>\n		<txp:if_section name=\"\">\n			<script src=\"<txp:site_url />/template/js/CA.js\"></script>\n		</txp:if_section>\n	</txp:cbe_frontauth_if_connected>','2015-11-20 16:08:47'),
	('start','misc','<!doctype html>\n<!-- paulirish.com/2008/conditional-stylesheets-vs-css-hacks-answer-neither/ -->\n<!--[if lt IE 7]> <html class=\"no-js lt-ie9 lt-ie8 lt-ie7\" lang=\"fr\"> <![endif]-->\n<!--[if IE 7]>    <html class=\"no-js lt-ie9 lt-ie8\" lang=\"fr\"> <![endif]-->\n<!--[if IE 8]>    <html class=\"no-js lt-ie9\" lang=\"fr\"> <![endif]-->\n<!-- Consider adding a manifest.appcache: h5bp.com/d/Offline -->\n<!--[if gt IE 8]><!--> <html class=\"no-js\" lang=\"fr\"> <!--<![endif]-->\n\n<head>\n	<meta charset=\"utf-8\" />\n	<meta name=\"author\" content=\"sacripant - Thomas Jund\" />\n	<meta name=\"copyright\" content=\"¬©sacripant.fr\" />\n	<meta name=\"description\" content=\"Memento, gesitonnaire de projet sous Textpattern\" />\n	\n	<title>\n		<txp:if_individual_article>\n			<txp:title /> ‚Äì\n		</txp:if_individual_article>\n		Memento\n	</title>\n	\n<!-- affichage favicon -->\n	<!-- Chrome, Safari, IE -->\n	<link rel=\"shortcut icon\" href=\"<txp:site_url />favicon.ico\">\n\n	<!-- Firefox, Opera (Chrome and Safari say thanks but no thanks) -->\n	<link rel=\"icon\" href=\"<txp:site_url />favicon.png\">\n\n<!-- CSS -->\n\n	<link rel=\"stylesheet\" href=\"<txp:site_url />template/css/fonts.css\" media=\"all\" />\n	<link rel=\"stylesheet\" href=\"<txp:site_url />template/css/reset.css\" media=\"all\" />\n	<link rel=\"stylesheet\" href=\"<txp:site_url />template/css/typography.css\" media=\"all\" />\n	<link rel=\"stylesheet\" href=\"<txp:site_url />template/css/forms.css\" type=\"text/css\" media=\"screen\" />\n	<link rel=\"stylesheet\" href=\"<txp:site_url />template/css/default.css\" type=\"text/css\" media=\"screen\" />	\n	\n<txp:hide>	<txp:css name=\"reset,typography,forms,default\" format=\"link\" /></txp:hide>\n	\n	\n	<!-- MODERNIZR, VERSION DE DEV, NON COMPRESS√â ET FULL -->\n	<txp:hide>	<script src=\"js/modernizr-2.5.3.js\"></script></txp:hide>\n	<!-- Noter les fonctionnalit√©s utilis√© pour charger une version custom :\n		* html5shiv\n		* YepNope\n		* Class js/no-js \n	-->\n	\n\n\n<!-- Script permettant de rafraichir la page auto apr√®s mise √† jour des css - JS - html FACILITE L\'INTEGRAITON\n	LIGNES √Ä EFFACER LORS DE L\'INCORPORATION AU CMS - ou de la mise en ligne en prod -->\n<txp:hide>	<script src=\"<txp:site_url />template/js/live.js\"></script></txp:hide>\n	\n\n</head>\n\n','2015-11-23 16:37:44'),
	('odd_even','misc','<txp:hide>\n=======================\nbascule d\'une variable odd / even\n√† utiliser pour les liste, les tableaux\n=======================\n</txp:hide>\n\n<txp:if_variable name=\"odd-even\" value=\" even\">\n    <txp:variable name=\"odd-even\" value=\" odd\" />\n<txp:else />\n    <txp:variable name=\"odd-even\" value=\" even\" />\n</txp:if_variable>','2015-11-23 16:37:44'),
	('rah_eo_From-newTodo.html','misc','; Content-type: text/html\n<txp:cbe_frontauth_if_logged>\r\n	<txp:adi_gps />\r\n	\r\n	<h1>Ajouter une tache au projet <em><txp:smd_query column=\"Title\" table=\"textpattern\" where=\'ID=<txp:variable name=\"idProjet\" />\' >{Title}</txp:smd_query></em></h1>\r\n\r\n	<txp:variable name=\"statut\"><txp:smd_query column=\"value\" table=\"custom_fields\" where=\"name=\'custom_2_set\' ORDER BY value\" break=\",\">{value}</txp:smd_query></txp:variable>\r\n\r\n	<txp:mem_simple_form table=\"textpattern\" id_field=\"ID\">\r\n			<txp:mem_form_secret name=\"string_Section\" value=\'todo\' />\r\n			<txp:mem_form_secret name=\"now_Posted\" value=\'\' />\r\n			<txp:mem_form_secret name=\"now_LastMod\" value=\'\' />\r\n			<txp:mem_form_secret name=\"now_feed_time\" value=\'\' />\r\n			<txp:mem_form_secret name=\"string_AuthorID\" value=\'<txp:cbe_frontauth_whois />\' />\r\n			<txp:mem_form_secret name=\"string_LastModID\" value=\'<txp:cbe_frontauth_whois />\' />\r\n			<txp:mem_form_secret name=\"randuid_uid\" value=\'\' />\r\n			<txp:mem_form_secret name=\"string_custom_1\" value=\'<txp:variable name=\"idProjet\" />\' />				\r\n\r\n			<div class=\"form-item\">\r\n				<txp:mem_form_textarea rows=\"2\" cols=\"30\" name=\"string_Title\" label=\"Titre\" break=\"\" default=\'\' />					\r\n			</div>\r\n			<div class=\"form-item\">\r\n				<txp:mem_form_select_category name=\"string_category1\" label=\"Categorie\" break=\"\" />\r\n			</div>\r\n			<div class=\"form-item\">\r\n				<txp:mem_form_select name=\"string_custom_2\" items=\'<txp:variable name=\"statut\" />\' values=\'<txp:variable name=\"statut\" />\' label=\"Statut\" break=\"\" />\r\n			</div>\r\n			<div class=\"form-item\">\r\n				<txp:mem_form_submit button=\"1\" label=\"Save\" />\r\n			</div>\r\n	</txp:mem_simple_form>\r\n	\r\n	<txp:mem_simple_if_ps name=\"string_Title\">\r\n		<txp:variable name=\"titleUrl\"><txp:smd_wrap transform=\"sanitize|url_title\" item=\'<txp:mem_simple_ps name=\"string_Title\" />\' /></txp:variable>\r\n		<txp:smd_query query=\'UPDATE textpattern \r\n			SET url_title =\'\'<txp:variable name=\"titleUrl\" />\'\'\r\n	   	WHERE uid=\'\'<txp:mem_simple_ps name=\"randuid_uid\" />\'\';\' \r\n	   />\r\n	</txp:mem_simple_if_ps>\r\n	\r\n</txp:cbe_frontauth_if_logged>		','2012-07-17 11:57:10'),
